import React, { useContext } from 'react';
import "./HeroCatalog.css";
import { ShopContext } from '../../Context/ShopContext';
import dropdown_icon from "../Assets/dropdown_ic"

const HeroCatalog = (props) => {
    const {all_product} = useContext(ShopContext)
    return (
        <div className="catalog-indexSort">
            <p>
                <span>Showing 12 of 12 products</span>
            </p>
            <div className="catalog-sortItems">
                Sort by <img src={dropdown_icon} alt="" />
            </div>
        </div>

    )
}

export default HeroCatalog
